import React, {Fragment} from 'react'
import styles from './Meals.module.css'
import Card from '../UI/Card'
import AddMealButton from './AddMealButton'

const MealItem = () =>{

    const allMeals = [
        {
            id: 'meal1',
            name: 'Pizza',
            description: 'the perfect italian way',
            price: '14.99'
        },
        {
            id: 'meal2',
            name: 'Bibimbap',
            description: 'the taste of korea',
            price: '13.99'
        },
        {
            id: 'meal3',
            name: 'Tacos',
            description: 'awesome mexican food',
            price: '12.99'
        }
    ]

    console.log(allMeals)



    const prices = allMeals.map((x) => x.price)
    console.log(prices)




    return (
        <Fragment>
            <Card>
                <ul>
                        {allMeals.map(meal => (
                            <li 
                            className={styles.mealItem}
                            key={meal.id}
                            name={meal.name}
                            description={meal.description}
                            price={meal.price}
                            >
                                <div className={styles.mealInfoLeft}>
                                    <h1> {meal.name}</h1>
                                    <h2> {meal.description}</h2>
                                    <h2> $ {meal.price}</h2>
                                </div>

                                <div className={styles.mealInfoMiddle}>
                                <h1> FOTO </h1>
                                </div>

                                <div className={styles.mealInfoRight}>
                                <AddMealButton/>
                                </div>


                            </li>

                        ))}
                    </ul>

              </Card>


        </Fragment>
    )
}

export default MealItem;
